  let config = Benchmark.Configuration(
      metrics: [BenchmarkMetric.wallClock],
      //warmupIterations: 10,
      maxDuration: .seconds(120)
      //maxIterations: 30
  )
  sizes.forEach { size in
    Benchmark("run(size:\(size))", configuration: config) { benchmark in
      blackHole(Rays.run(size: size))
    }
  }
}
